Currently this shader dont look like disolve at all. Maybe this example may help?  Shader "Custom/Dissolve" {
    Properties{
        _MainTex("Texture", 2D) = "white" {}
        _DissolveThreshold("Dissolve Threshold", Range(0, 1)) = 0
        _EdgeColor("Edge Color", Color) = (0, 0, 0, 0)
    }

        SubShader{
            Tags { "Queue" = "Transparent" "RenderType" = "Transparent" }
            LOD 100

            Pass {
                CGPROGRAM
                #pragma vertex vert
                #pragma fragment frag
                #pragma multi_compile_fog
                #include "UnityCG.cginc"

                struct appdata {
                    float4 vertex : POSITION;
                    float2 uv : TEXCOORD0;
                };

                struct v2f {
                    float2 uv : TEXCOORD0;
                    UNITY_FOG_COORDS(1)
                    float4 vertex : SV_POSITION;
                };

                sampler2D _MainTex;
                float _DissolveThreshold;
                float4 _EdgeColor;

                v2f vert(appdata v) {
                    v2f o;
                    o.vertex = UnityObjectToClipPos(v.vertex);
                    o.uv = v.uv;
                    UNITY_TRANSFER_FOG(o,o.vertex);
                    return o;
                }

                fixed4 frag(v2f i) : SV_Target {
                    float noiseScale = 1.0;
                    fixed noise = frac(sin(dot(i.uv * noiseScale ,float2(12.9898,78.233))) * 43758.5453);
                    noise = noise * 0.5 + 0.5; // Remap the noise

                    fixed4 col = tex2D(_MainTex, i.uv);
                    fixed4 edgeCol = _EdgeColor;
                    edgeCol.a = col.a;

                    if (noise < _DissolveThreshold) {
                        clip(-1);
                    }
                    else if (noise < _DissolveThreshold + 0.1) {
                        return edgeCol;
                    }

                    UNITY_APPLY_FOG(i.fogCoord, col);
                    return col;
                }
                ENDCG
            }
        }
}

using Core.Extensions;
using DG.Tweening;
using UnityEngine;

namespace Core._.UI
{
    public class ShadowMoveTween : TweenableBase
    {
        [SerializeField] private bool _isInverted = false;
        [SerializeField] private Vector3 _startPos;
        [SerializeField] private Vector3 _endPos;
        [SerializeField] private float _duration = 0.3f;
        public override Tween Tween { get; set; }

        public override Tween CreateTween()
        {
            var sequence = DOTween.Sequence();
            var rect = (RectTransform)transform;
            sequence.Append(rect.DOAnchorPos(_startPos, 0f));
            sequence.Append(rect.DOAnchorPos(_endPos, _duration).SetEase(Ease.InOutQuad));

            if (!_isInverted) sequence.SetInverted(true);
            return sequence;
        }

        public void Play(bool isInverted)
        {
            Tween?.Complete();
            _isInverted = isInverted;
            Tween = CreateTween();
        }
    }
}
